sacr_tempo_processo_others = '''SELECT DH_PROCESSO, CD_TIPO_TEMPO_PROCESSO, CD_ATENDIMENTO, NM_USUARIO FROM DBAMV.VW_EXIMIO_SACR_TEMPO_PROCESSO WHERE DH_PROCESSO >= TO_DATE('{data_ini}', 'DD/MM/YYYY') AND DH_PROCESSO < TO_DATE('{data_fim}', 'DD/MM/YYYY') + INTERVAL '1' DAY AND CD_TIPO_TEMPO_PROCESSO IN (30,20,32,99,50)'''

query_documento_clinico_others = "SELECT CD_OBJETO, cd_atendimento, cd_tipo_documento, tp_status, DH_CRIACAO FROM DBAMV.VW_EXIMIO_PW_DOCUMENTO_CLINICO WHERE DH_CRIACAO >= TO_DATE('{data_ini}', 'DD/MM/YYYY') AND DH_CRIACAO < TO_DATE('{data_fim}', 'DD/MM/YYYY') + INTERVAL '1' DAY AND CD_TIPO_DOCUMENTO != 36"

query_update_all_cid = ''' SELECT CD_ATENDIMENTO, A.CD_MULTI_EMPRESA, CD_CID FROM DBAMV.VW_EXIMIO_ATENDIME A WHERE DT_ATENDIMENTO >= TO_DATE('{data}', 'DD/MM/YYYY') AND DT_ATENDIMENTO < TO_DATE('{data}', 'DD/MM/YYYY') + INTERVAL '1' DAY '''